$(document).ready(function() {
    $("#div1show").click(function() {
        $("#camera").css("display","none");
        $("#headphone").css("display","none");
        $("#phone").css("display","none");
        $("#watch").css("display","none");
        $("#shoe").css("display","none");
        $("#all").css("display","block");
    });
});
